import DocumentFragment from '../document-fragment/DocumentFragment';
/**
 * ShadowRoot.
 */
export default class ShadowRoot extends DocumentFragment {
    mode: string;
    /**
     * Returns inner HTML.
     *
     * @return {string} HTML.
     */
    /**
    * Sets inner HTML.
    *
    * @param {string} html HTML.
    * @return {string} HTML.
    */
    innerHTML: string;
    /**
     * Converts to string.
     *
     * @return {string} String.
     */
    toString(): string;
}
