/**
 * Utility for scoping CSS inside a shadow root.
 */
export default class StyleScoper {
    /**
     * Scopes CSS.
     *
     * @param css CSS code.
     * @param id Unique id to use.
     * @param tagName Tag name.
     * @return Scoped CSS.
     */
    static scope(css: string, id: string, tagName: string): string;
}
